CREATE TABLE IF NOT EXISTS `muln_goal` (`goal_id` INTEGER(11) NOT NULL auto_increment , `name` VARCHAR(255) NOT NULL, `description` TEXT NOT NULL, `parentid` INTEGER(11) NOT NULL DEFAULT 0, `isdeleted` TINYINT(1) DEFAULT false, `createdby` INTEGER(11) NOT NULL, `createdon` DATETIME NOT NULL, `updatedby` INTEGER(11) NOT NULL, `updatedon` DATETIME NOT NULL, `deletedby` INTEGER(11), `deletedon` DATETIME, PRIMARY KEY (`goal_id`)) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `muln_mapping` (`mapping_id` INTEGER(11) NOT NULL auto_increment , `fk_goal` INTEGER(11) DEFAULT 0, `fk_sub_goal` INTEGER(11) DEFAULT 0, `fk_targetid` INTEGER(11) NOT NULL, `type` INTEGER(11) NOT NULL, `createdby` INTEGER(11) NOT NULL, `createdon` DATETIME NOT NULL, `updatedby` INTEGER(11) NOT NULL, `updatedon` DATETIME NOT NULL, `deletedby` INTEGER(11), `deletedon` DATETIME, PRIMARY KEY (`mapping_id`)) ENGINE=InnoDB;